function trackEvent(e, n) { if (console.info(`track element: ${e.id} category: ${n}`), typeof gtag !== 'undefined' && typeof gtag === 'function') { new Promise((t, o) => { try { gtag('event', 'click', { event_category: n, event_label: e.id, value: '1' }), t('track done'); } catch (e) { o(new Error(e)); } }).then((e) => { console.info(e); }).catch((e) => { console.error(`Error tracking. ${e}`); }); } else console.info('track not enabled'); }
